import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useCartContext } from "./context/CartContext.jsx";

const CartWidget = () => {
  const {cart} = useCartContext()
  const [contrador, setcontrador] = useState(0)




  useEffect(() => {
    setcontrador (cart.reduce((a,v) =>  a = a + v.quantity , 0 ))
  }, [{contrador}]); 
  console.log(contrador);

  return (
    <div className="dropdown dropdown-end">
    <label tabIndex="0" className="btn btn-ghost btn-circle">
      <div className="indicator">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" /></svg>
        {contrador.length > 0 && <span className="badge badge-sm indicator-item">{contrador}</span>}

      </div>
    </label>
    <div tabIndex="0" className="mt-3 card card-compact dropdown-content w-52 bg-base-100 shadow">
      <div className="card-body">
        <span className="font-bold text-lg">Cantidad : {contrador} </span>
        <span className="text-info">Subtotal: {cart.reduce((sum, i) => (
        sum += i.quantity * i.price
    ), 0)} </span>
        <div className="card-actions">
        <Link to={`/cart`} className="btn btn-primary btn-block" >
                          Visitar el carrito
                        </Link>

        </div>
      </div>
    </div>
  </div>
  )
}
export default CartWidget
