import React, { useEffect, useState } from 'react';
import Item from './Item';
import { productList } from '../data/data.js';
import './styles/ItemList.css';
import { collection, doc, getDoc, getDocs, getFirestore } from "firebase/firestore";
import { useAppContext } from './context/AppContext';
import NavBar from './NavBar';
import Footer from './Footer';



const ItemList = () => {

  const [products, setProducts] = useState([]);

  const {products1} = useAppContext()

 //Entrega al '02/05



  useEffect(() => {
    setTimeout(() => {
      const db = getFirestore()
    const itemsCollection = collection(db,'items')
    getDocs (itemsCollection).then(snapshot => { 
        const productList = []
       snapshot.docs.forEach(s=> { 
           //console.log(s.data());
           productList.push({id : s.id,...s.data()})
       })

       setProducts(productList) 
     })
    }, 22000);


  }, []); 



  return (
    <div>
      <NavBar></NavBar>
      <div className="asd2">
      {

        products.length ? ( 
          <div className='grid grid-cols-5 gap-4'>
          <>
            {

              products.map((product) => {

                return (

                  <div key={product.id} className="grid justify-center grid-cols-6">
                    <Item
                      name={product.name}
                      thumbnail={product.thumbnail}
                      price={product.price}
                      stock={product.stock}
                      id={product.id}
                      tub2={product.tub2}
                    />
                  </div>
                );
              })
            }
          </>
          </div>
        ) : (
          <img className=''  src="https://i.pinimg.com/originals/65/ba/48/65ba488626025cff82f091336fbf94bb.gif" />
        ) 
      }
    </div>
    </div>
  );
};
export default ItemList;
