import { useEffect, useState } from "react";
import { useParams } from "react-router-dom"
import { productList } from '../data/data.js';

const ItemDetail = () => {
  const  {id} = useParams( )
  const [juego, setJuego] = useState({})

  



  const getJuegos = new Promise((resolve, reject) => {
    setTimeout(() => {
      resolve(productList);
    }, 2000);
  });


  const getJuegosFromDB = async () => {
    try {
      const result = await getJuegos;
      setJuego (productList.find  (m => m.id == id));
    } catch (error) {
      console.log(error);
      alert('No podemos mostrar los productos en este momento');
    }
  };

  useEffect(() => {
    getJuegosFromDB();
  }, []); 




  return (

    <div className="grid grid-cols-4 gap-4">
    {
      
      juego !== {} ? ( 
        <>
          
              
              
              <div>
                <div>ItemDetail : {id}</div>
                <li>{juego.id}</li>
                <li>{juego.name}</li>
                <li>{juego.consola}</li>
              </div>
              
              
            
          
        </>
      ) : (
        <img className='loader' src="https://icon-library.com/images/loading-icon-transparent-background/loading-icon-transparent-background-12.jpg" />
      ) 
    }
  </div>
    
  

    
  
  )
}
export default ItemDetail
