import { useEffect, useState } from "react";
import { useParams } from "react-router-dom"
import { productList } from '../data/data.js';

const ItemDetail = () => {
  const  {id} = useParams( )
  const [juego, setJuego] = useState({})
  useEffect(() => {
    //aca si quiero varios puedo usar el filter, find para 1 solo
    setJuego (productList.find  (m => m.id == id))
  }, [id])
  
  return (
    <>
    <div>ItemDetail : {id}</div>
    <li>{juego.id}</li>
    <li>{juego.name}</li>
    <li>{juego.consola}</li>

    
    </>
  )
}
export default ItemDetail
