import React, { useEffect, useState } from 'react';
import Item from './Item';
import { productList } from '../data/data.js';
import './styles/ItemList.css';
import { collection, doc, getDoc, getDocs, getFirestore } from "firebase/firestore";
import { useAppContext } from './context/AppContext';
import NavBar from './NavBar';



const ItemList = () => {

  const [products, setProducts] = useState([]);

  const {products1} = useAppContext()

 //Entrega al '02/05



  useEffect(() => {
    setTimeout(() => {
      const db = getFirestore()
    const itemsCollection = collection(db,'items')
    getDocs (itemsCollection).then(snapshot => { 
        const productList = []
       snapshot.docs.forEach(s=> { 
           //console.log(s.data());
           productList.push({id : s.id,...s.data()})
       })

       setProducts(productList) 
     })
    }, 2000);


  }, []); 



  return (
    <div>
      <NavBar/>
      <div className="grid grid-cols-4 gap-4">

      {

        products.length ? ( 
          <>
            {

              products.map((product) => {

                return (
                  <div key={product.id} className="flex justify-center grid-cols-4">
                    <Item
                      name={product.name}
                      thumbnail={product.thumbnail}
                      price={product.price}
                      stock={product.stock}
                      id={product.id}
                      tub2={product.tub2}
                    />
                  </div>
                );
              })
            }
          </>
        ) : (
          <img className='loader' style={{width: '100%',height: '50%'}} src="https://zachariasdayneskc32354.files.wordpress.com/2015/05/loaddding.gif" />
        ) 
      }
    </div>
    </div>

  );
};

export default ItemList;
