import { useCartContext } from "./context/CartContext.jsx";



const Cart = () => {
  const {cart} = useCartContext()
  return (
    <>
    {
      
      cart.map((juegosCons) => {
        
        return (
            
          <div key={cart.id} className=" bajar flex justify-center grid-cols-4">
            <Item
              name={cart.name}
              thumbnail={cart.thumbnail}
              price={cart.price}
              stock={cart.stock}
              id={cart.id}
            />
          </div>
        );
      })
    }
  </>
  )
}
export default Cart
